from enum import Enumclass States(Enum):    STATE_START = 1    STATE_ID = 2    STATE_COLON_ASSIGNMENT = 3    STATE_EQUALS_ASSIGNMENT = 4    STATE_SQUARE_BRACKET_OPEN = 5    STATE_SIGN = 6    STATE_INTEGER = 7    STATE_SPACE_4_2 = 8    STATE_COMMA = 9    STATE_SQUARE_BRACKET_CLOSE = 10    STATE_MULTIPLY = 11    STATE_BRACKET_OPEN = 12    STATE_SPACE_8_2 = 13    STATE_BRACKET_CLOSE = 14    STATE_SEMICOLON = 15    STATE_ERROR = 16current_state = States.STATE_STARTdef leks_analyze(leksema):    global current_state    if current_state == States.STATE_START:        if leksema[1] == "буква":            current_state = States.STATE_ID